package hashtable;

import java.util.HashMap;
import java.util.HashSet;
import java.util.Hashtable;
import java.util.Map;

public class FindOccur {
    public static void main(String[] args) {

        int[] arr = {1, 1, 2, 2, 2, 1};
//        System.out.println(maxDistance(arr));
        String s = "";
        String t = "";

        System.out.println(findTheDiff(s,t));

    }

    public static int maxDistance(int[] arr){
        var max = 0;
        HashMap<Integer,Integer> hashtable = new HashMap<>();

        for(int i=0;i<arr.length;i++){

            if(!hashtable.containsKey(arr[i]))
                hashtable.put(arr[i],i);
            else
                max = Math.max(max,i - hashtable.get(arr[i]));
        }
        return max;

    }
    public static char findTheDiff(String s,String  t){
        HashMap<Character,Integer> hs = new HashMap<>();
        for(int i=0;i<s.length();i++){
           if(!(hs.containsKey(s.charAt(i)))){
               hs.put(s.charAt(i),1);
           }else{
               int var = hs.get(s.charAt(i));
               hs.replace(s.charAt(i),var,var+1);
           }
        }


       for(int i=0;i<t.length();i++){
           if(!(hs.containsKey(t.charAt(i)))){
               return t.charAt(i);
           }else if((hs.containsKey(t.charAt(i)) && hs.get(t.charAt(i)) > 0 )){
               int var = hs.get(t.charAt(i));
               hs.replace(t.charAt(i),var,var-1);
           }else{
               return t.charAt(i);
           }
       }

        return ' ';
    }
}
